import groovy.json.JsonSlurper;

def slurper = new JsonSlurper()
def result = slurper.parseText(messageExchange.responseContent)

assert result.incidentType == 'test'

import groovy.json.JsonSlurper;

def slurper = new JsonSlurper()
def result = slurper.parseText(messageExchange.responseContent)

assert result.incidentType == 'test'

// set incidentId
context.getTestCase().
	getTestStepByName("Properties").
		getProperty("incidentId").
			setValue(result.id + "");

